export const Listener = () => {
    const [clicked, setClicked] = useState(false);
    const handle = useHandle('ClickState')
    const numClicks = handle.get('clicks');

    const remoteClicked = e => {
        if (numClicks < e.get('clicks')) {
            setClicked(true);
